    $(document).on('click', '.clear_content', function(){
        $(this).parent().find('input').val('');
        //tt-input its a class for typeahead auto-suggest
        if ($(this).parent().find('input').hasClass("tt-input")) {
            $(this).parent().find('input').typeahead('val','');
        }
